mapper.select:
select
  student_uid, student_id, student_password, student_name, student_tutorid, student_email, student_phone
from weeklyreport.student


        and student_uid = #{studentUid}


        and student_id = #{studentId}


        and student_password = #{studentPassword}


        and student_name = #{studentName}


        and student_tutorid = #{studentTutorid}


        and student_email = #{studentEmail}


        and student_phone = #{studentPhone}
