mapper.select:
select
  tutor_uid, tutor_id, tutor_password, tutor_name, tutor_teamname, tutor_rank, tutor_phone
from weeklyreport.tutor


        and tutor_uid = #{tutorUid}


        and tutor_id = #{tutorId}


        and tutor_password = #{tutorPassword}


        and tutor_name = #{tutorName}


        and tutor_teamname = #{tutorTeamname}


        and tutor_rank = #{tutorRank}


        and tutor_phone = #{tutorPhone}
